import { useEffect, useState } from 'react';
import IDataList from '../model/IDataList';
import { getDataFromServer } from '../services/Menu';
import AddForm from './AddForm';

function ShowData() {
    const [items, setItems] = useState<IDataList[]>([]);
    const [error, setError] = useState<Error|null>(null);
    const [sum, setSum] = useState<number|null>(0);
    const [rahulTotal, setRahulTotal] = useState<number>(0);
    const [rameshTotal, setRameshTotal] = useState<number>(0);
    const [showForm, setShowForm] = useState<Boolean>(false);

    var rahulSpent: number = 0;
    var rameshSpent: number = 0;

    useEffect(() => {
        const fetchMenu = async() => {
            try {
                const data = await getDataFromServer();
                setItems(data);
                setSum(data.reduce((result, v) => result=result+v.price, 0)); 
                calculateShares(data);
            } catch(error: any) {
                setError(error)
            }
        }
        fetchMenu();
    });

    const calculateShares = (data : IDataList[]) => {
        data.map(record => (record.payeeName === 'Rahul' ? 
                            (rahulSpent = rahulSpent + record.price): 
                            (rameshSpent = rameshSpent + record.price)
                           )
                );
        setRahulTotal(rahulSpent);
        setRameshTotal(rameshSpent);
    }

    const success = () => {setShowForm(true)}
    const cancel = () => {setShowForm(false)}

    return (
        <>
            <header id="page-header">
                <h1>Expense Tracker</h1>
            </header>
            <div className="list-container">
                <button id="add-button" onClick={success}>Add</button>
                <table>
                    <thead>
                        <tr>
                            <td>Date</td>
                            <td>Product purchased</td>
                            <td>Price</td>
                            <td>Payee</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            items && items.map((item, idx) => (
                                <tr key={idx}>
                                    <td>{item.setDate.toString()}</td>
                                    <td>{item.product}</td>
                                    <td>{item.price}</td>
                                    <td>{item.payeeName}</td>
                                </tr>
                            ) )
                        }
                    </tbody>
                </table>
            </div>
            {
                error && <div>{error.toString()}</div>
            }
            <hr></hr>
            <div>Total:</div>
            <span>{sum}</span><br/>
            <div>Rahul paid:</div>
            <span>{rahulTotal}</span><br/>
            <div>Ramesh paid:</div>
            <span>{rameshTotal}</span><br/>
            <span>Pay</span><span>{rahulTotal > rameshTotal ? "Rahul": "Ramesh"}:</span>
            <span>{(Math.abs(rahulTotal - rameshTotal)/2).toString()}</span>
            {
                //todo
                showForm && <div className="form-div"><AddForm onShow={success} onClose={cancel}/></div>
            }
        </>
    )
}
export default ShowData;
