import { ChangeEvent, FormEvent, ReactElement, useState } from "react";
import IDataList from "../model/IDataList";
import { postDataToServer } from "../services/Menu";
import FormElement from "./FormElement";
type Props = {
    onShow : any,
    onClose : any
}

const AddForm = ( props : Props) => {

    const [payeeName, setPayeeName] = useState<string>('');
    const [product, setProduct] = useState<string>('');
    const [price, setPrice] = useState<number>(0);
    const [date, setDate] = useState<string>('');

    const setPayee = (event: ChangeEvent<HTMLSelectElement>) => {
        setPayeeName(event.target.value)
    }

    const setProductF = (event: ChangeEvent<HTMLInputElement>) => {
        setProduct(event.target.value)
    }

    const setPriceF = (event: ChangeEvent<HTMLInputElement>) => {
        setPrice(parseInt(event.target.value))
    }

    const setDateF = (event: ChangeEvent<HTMLInputElement>) => {
        setDate(event.target.value)
    }

    const submitHandler = async (event : FormEvent<HTMLFormElement>) => {
        event.preventDefault()
        var parsedDate : Date = new Date();
        parsedDate.setFullYear(parseInt(date.substring(0, 4)));
        parsedDate.setMonth(parseInt(date.substring(5, 7)) - 1);
        parsedDate.setDate(parseInt(date.substring(8, 10)));
        console.log("date is " + parsedDate.toDateString());
        const finalData : IDataList = {
            payeeName: payeeName,
            product: product,
            price: price,
            setDate: parsedDate
        }
        const data = await postDataToServer(finalData)
        props.onShow()
    }

    return (
        <div>
            <header>
                <h1>Add New Item</h1>
            </header>

            <form action="submit" onSubmit={submitHandler}>
                <FormElement 
                    label="Name" 
                    inputElement=
                        {
                            <select name="select" id="payeeName" required value={payeeName} onChange={setPayee}>
                                <option value="choose" defaultChecked>Choose</option>
                                <option value="rahul">Rahul</option>
                                <option value="ramesh">Ramesh</option>
                            </select>
                        } 
                />
                <FormElement label="Product purchased" inputElement={<input type="text" value={product} onChange={setProductF}></input>} />
                <FormElement label="Price" inputElement={<input type="number" value={price} onChange={setPriceF}></input>} />
                <FormElement label="Date" inputElement={<input type="text" value={date} onChange={setDateF}></input>} />
                <button type="button" id="submit">Submit</button>
                <button id="close" onClick={props.onClose}>Close</button>
            </form>
        </div>
    )
}

export default AddForm;
